void write_data_bolang()
{
	int i=0,j=0,k=61;
	unsigned char tempL,tempH;
	//unsigned char ram_ok[][]=ram;
	for(j=0;j<54;j++)
	{
		for(i=0;i<16;i++)
		{
			tempH = (unsigned char)((ram_green[j][i]&0xff00)>>8);
			tempL = (unsigned char)(ram_green[j][i]&0x00ff);
			led[k] = tempH;
			led[++k] = tempL;
			if(k==64){
				printf("led[61] = %x,led[62] = %x\n",led[61],led[62]);
				printf("led[63] = %x,led[64] = %x\n",led[63],led[64]);
			}
			k++;
		}
	}
}
#define ET6296_POWER_DOWN	0x22
#define ET6296_ALL_OFF		0x33
#define ET6296_ALL_OPEN		0x44
#define ET6296_ALL_MANU		0x55
#define ET6296_ALL_AUTO		0x66
#define ET6296_USER_MODE	0x77

int main(int argc, char** argv)
{
    int fd = -1;
	int i ;
	//led[7]=0x10;
	for(i=0;i<7;i++)
		led[i] = 0x0 ;
	for(i=0;i<54;i++)
		led[7+i] = reg_g[i] ;
    write_data_bolang();
    fd = open(DEVICE_NAME, O_RDWR);
    if(fd == -1) {
        printf("Failed to open device %s\n", DEVICE_NAME);
        return -1;
    }
	//ioctl(fd,ET6296_ALL_OFF,0);
	ioctl(fd,ET6296_USER_MODE,led);
	ioctl(fd,ET6296_ALL_OPEN,0);
    close(fd);
    return 0;
}